mapper.insert:
insert into ClassSpaceComment (ClassSpaceId, UserId, 
  CommentContent, ReplyId, CommentDate, 
  ReplyStatus, ReplyDate, Status
  )
values (#{classspaceid,jdbcType=INTEGER}, #{userid,jdbcType=INTEGER}, 
  #{commentcontent,jdbcType=VARCHAR}, #{replyid,jdbcType=INTEGER}, #{commentdate,jdbcType=TIMESTAMP}, 
  #{replystatus,jdbcType=TINYINT}, #{replydate,jdbcType=TIMESTAMP}, #{status,jdbcType=TINYINT}
  )

mapper.select:
select COUNT(cc.TabId) 
from ClassSpaceComment cc 
where cc.Status = 1 and cc.ReplyStatus = 0 and cc.ReplyId = #{0}
