mapper.insert:
insert into ClassSpace (TabId, ClassId, MsgContent, 
  PublisherId, PublishDate, Status
  )
values (#{tabid,jdbcType=INTEGER}, #{classid,jdbcType=INTEGER}, #{msgcontent,jdbcType=VARCHAR}, 
  #{publisherid,jdbcType=INTEGER}, #{publishdate,jdbcType=TIMESTAMP}, #{status,jdbcType=TINYINT}
  )

mapper.select:
select COUNT(c.TabId) 
from SpaceContentStatus ss,ClassSpace c 
where ss.SpaceId=c.TabId and ss.IsRead=0 and c.Status=1 and ss.SpaceType=1 and ss.UserId = #{0}

  	select count(ss.IsLike) likeCnt 
  	from SpaceContentStatus ss 
	where ss.SpaceType=1 and ss.SpaceId = #{0} and ss.UserId = #{1}

select ss.UserId 
from SpaceContentStatus ss 
where ss.SpaceType = 1 and ss.IsLike = 1 and ss.SpaceId = #{0}

select c.TabId,c.ClassId,c.MsgContent,c.PublisherId,c.PublishDate 
from ClassSpace c 
where c.Status = 1 and c.TabId = #{0}
