mapper.insert:
insert into ClassSpace (ClassId, MsgContent, 
  EncType, EncAddr, EncImgAddr, 
  PublisherId, PublishDate, Status
  )
values (#{classid,jdbcType=INTEGER}, #{msgcontent,jdbcType=VARCHAR}, 
  #{enctype,jdbcType=TINYINT}, #{encaddr,jdbcType=VARCHAR}, #{encimgaddr,jdbcType=VARCHAR}, 
  #{publisherid,jdbcType=INTEGER}, #{publishdate,jdbcType=TIMESTAMP}, #{status,jdbcType=TINYINT}
  )

mapper.select:
select COUNT(c.TabId) 
from SpaceContentStatus ss,ClassSpace c 
where ss.SpaceId=c.TabId and ss.IsRead=0 and c.Status=1 and ss.SpaceType=2 and ss.UserId = #{0}

select COUNT(c.TabId) 
from SpaceContentStatus ss,ClassSpace c 
where ss.SpaceId=c.TabId and ss.IsRead=0 and c.Status=1 and ss.SpaceType=2 and ss.UserId = #{0}
and c.ClassId =#{1}

select top(#{3}) c.TabId,c.ClassId,c.MsgContent,c.PublisherId,c.PublishDate 
,c.EncType,c.EncAddr,c.EncImgAddr
from SpaceContentStatus ss,ClassSpace c 
where ss.SpaceId=c.TabId and ss.IsRead=0 and c.Status=1 and ss.SpaceType=#{1} and ss.UserId = #{0}
and c.ClassId =#{4}
and c.TabId not in (select top((#{2}-1) * #{3}) c.TabId from SpaceContentStatus ss,ClassSpace c 
where ss.SpaceId=c.TabId and ss.IsRead=0 and c.Status=1 and ss.SpaceType=#{1} and ss.UserId = #{0}
order by c.PublishDate desc)
order by c.PublishDate desc

select ss.UserId 
from SpaceContentStatus ss 
where ss.SpaceType = 2 and ss.IsLike = 1 and ss.SpaceId = #{0}

select count(c.TabId) 
from ClassSpace c 
where c.Status = 1 and c.ClassId = #{0}
