mapper.insert:
insert into Note (TabId, StudentId, MsgContent, 
  PublisherId, PublishDate, Status
  )
values (#{tabid,jdbcType=INTEGER}, #{studentid,jdbcType=INTEGER}, #{msgcontent,jdbcType=VARCHAR}, 
  #{publisherid,jdbcType=INTEGER}, #{publishdate,jdbcType=TIMESTAMP}, #{status,jdbcType=TINYINT}
  )

mapper.select:
select COUNT(n.TabId) 
from SpaceContentStatus s,Note n 
where s.SpaceId=n.TabId and s.IsRead=0 and n.Status=1 and s.SpaceType=1 and s.UserId = #{0}

select n.StudentId,n.MsgContent,n.PublisherId,n.PublishDate 
from SpaceContentStatus s,Note n 
where s.SpaceId=n.TabId and s.IsRead=0 and n.Status=1 and s.SpaceType=1 and s.UserId = #{0}

  	select s.IsLike 
  	from SpaceContentStatus s,Note n 
	where s.SpaceId=n.TabId and n.Status=1 and s.SpaceType=1 and n.TabId = #{0} and s.UserId = #{1}
