mapper.insert:
insert into Note (TabId, StudentId, MsgContent, 
  PublisherId, PublishDate, Status
  )
values (#{tabid,jdbcType=INTEGER}, #{studentid,jdbcType=INTEGER}, #{msgcontent,jdbcType=VARCHAR}, 
  #{publisherid,jdbcType=INTEGER}, #{publishdate,jdbcType=TIMESTAMP}, #{status,jdbcType=TINYINT}
  )

mapper.select:
select COUNT(n.TabId) 
from SpaceContentStatus ss,Note n 
where ss.SpaceId=n.TabId and ss.IsRead=0 and n.Status=1 and ss.SpaceType=1 and ss.UserId = #{0}

select ss.UserId 
from SpaceContentStatus ss 
where ss.SpaceType = 1 and ss.IsLike = 1 and ss.SpaceId = #{0}

select count(n.TabId) 
from Note n 
where n.Status = 1 and n.StudentId = #{0}

select n.TabId,n.StudentId,n.MsgContent,n.PublisherId,n.PublishDate 
from Note n 
where n.Status = 1 and n.TabId = #{0}
