mapper.insert:
insert into UserSpaceMsg (TabId, UserSpaceId, UserId, 
  MsgContent, ReplyId, MsgDate, 
  ReplyStatus, ReplyDate, Status
  )
values (#{tabid,jdbcType=INTEGER}, #{userspaceid,jdbcType=INTEGER}, #{userid,jdbcType=INTEGER}, 
  #{msgcontent,jdbcType=VARCHAR}, #{replyid,jdbcType=INTEGER}, #{msgdate,jdbcType=TIMESTAMP}, 
  #{replystatus,jdbcType=TINYINT}, #{replydate,jdbcType=TIMESTAMP}, #{status,jdbcType=TINYINT}
  )

mapper.select:
select COUNT(um.TabId) 
from UserSpaceMsg um 
where um.Status = 1 and um.ReplyStatus = 0 and um.ReplyId = #{0}
